import './NoteCard.css';

const simpleStyle: any = {
  width: '25px',
  textAlign: 'center',
}

const seventhStyle: any = {
  width: '50px',
  paddingLeft: '6px',
}


export const NoteCard = ({chord, seventh=false}: {chord: {id: string, char: string, type: {full: string, short: string, symbol: string}, num: string}, seventh?: boolean}) => {  
  return (
    <div className={'cardContent ' + chord.type.short} style={seventh ? seventhStyle : simpleStyle}>
      <div className='cardNumber' style={{backgroundColor: 'yellow'}}>{chord.num}</div>
      <div className='textContainer'>
        <div className='rootText' style={{backgroundColor: 'cyan'}}>{chord.char[0]}{chord.char.slice(1,)}</div>
        {seventh ? <div className='seventhText' style={{backgroundColor: 'magenta'}}>{chord.type.symbol}</div> : <></>}
      </div>
    </div>
  )
}
