import './NoteCard.css';

const simpleStyle: any = {
  width: '100%',
  textAlign: 'center',
  backgroundColor: '#ff00ff88',
}

const seventhStyle: any = {
  left: '6px',
  backgroundColor: '#00ffff88',
}

export const NoteCard = ({chord, seventh=false}: {chord: {id: string, char: string, type: {full: string, short: string, symbol: string}, num: string}, seventh?: boolean}) => {  
  return (
    <div className={'cardContent ' + chord.type.short} style={{width: seventh ? "50px" : "25px"}}>
      <div className='cardNumber' style={seventh ? seventhStyle : simpleStyle}>{chord.num}</div>
      <div className='textContainer'>
        <div className='rootText'>{chord.char[0]}{chord.char.slice(1,)}</div>
        {seventh ? <div className='seventhText'>{chord.type.symbol}</div> : <></>}
      </div>
    </div>
  )
}
