import './Bar.css';

import { ProgContext } from '../ModeContext';

export const Bar = () => {
  return (
    <div className='bar barBlur'>
      <ProgContext.Consumer>
        {
          (value: any) => value.map((chord: any) => (
            <div className={'cardContent ' + chord.type}>
              <div className='cardNumber'>{chord.num}</div>
              <div className='cardText'>{chord.char[0]}<span className='accidental'>{chord.char.slice(1,)}</span></div>
            </div>
          ))
            // {
            //   value.map((c: any) => (
            //     <div className='cardContent aug'>
            //       <div className='cardNumber'>I+</div>
            //       <div className='cardText'>C<span className='accidental'></span></div>
            //     </div>
            //   ))
            // }
        }
      </ProgContext.Consumer>
      <div className='cardContent aug'>
        <div className='cardNumber'>I+</div>
        <div className='cardText'>C<span className='accidental'></span></div>
      </div>
      <div className='cardContent maj'>
        <div className='cardNumber'>I</div>
        <div className='cardText'>C<span className='accidental'></span></div>
      </div>
      <div className='cardContent min'>
        <div className='cardNumber'>i</div>
        <div className='cardText'>C<span className='accidental'></span></div>
      </div>
      <div className='cardContent dim'>
        <div className='cardNumber'>io</div>
        <div className='cardText'>C<span className='accidental'></span></div>
      </div>
      <div className='emptyCard'>+</div>
    </div>
  );
}
