import { useContext, useState, useEffect } from 'react';
import { SortableContext, horizontalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { v4 as uuidv4 } from 'uuid';

import './Bar.css';

import { NoteCard } from '../NoteCard';
import { ProgContext } from '../ModeContext';
import { useDroppable } from '@dnd-kit/core';


const SortableItem = ({item}: any) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({id: item.id})

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  }

  const buttonStyle = {
    width: '30px',
    height: '40px', 
    margin: '3px', 
    border: isDragging ? '2px solid #000' : '2px solid #ababab80',
    backgroundColor: isDragging ? '#e0e0e0': '#eee',
  }

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <button style={buttonStyle}>
        {item.note}
      </button>
    </div>
  )
};


export const Bar = () => {

  const [items, setItems] = useState<Array<{id: string, char: string, type: string, num: string}>>([
    {id: uuidv4(), char: 'D', type: 'min', num: 'ii'},
    {id: uuidv4(), char: 'G', type: 'maj', num: 'V'},
    {id: uuidv4(), char: 'C', type: 'maj', num: 'I'},
  ]);

  const [uniqueBarId] = useState(uuidv4());
  const {setNodeRef} = useDroppable({id: uniqueBarId, data: {items: items, setItems: setItems}});

  // const items = useContext(ProgContext).items;

  return (
    <SortableContext
      items={items.map((i: any) => i.id)}
      strategy={horizontalListSortingStrategy} 
      id={'chordSortable'}
    >
      <div className='bar barBlur' ref={setNodeRef}>
        {items.map((chord: any) => <NoteCard chord={chord}/>)}
        <div className='emptyCard'>+</div>
      </div>
    </SortableContext>
  );
}
