import './Chart.css';
import { useState, useEffect } from 'react';
import { pitchSpeller } from './pitchSpeller';

const defaultScales = { 
  // diatonic modes:
  Lydian:     [0,2,4,6,7,9,11], 
  Ionian:     [0,2,4,5,7,9,11], 
  Mixolydian: [0,2,4,5,7,9,10], 
  Dorian:     [0,2,3,5,7,9,10], 
  Aeolian:    [0,2,3,5,7,8,10], 
  Phrygian:   [0,1,3,5,7,8,10], 
  Locrian:    [0,1,3,5,6,8,10],
}

const exampleOutput = {
  lydian: [
    {char: 'C', type: 'maj', num: 'I'},
    {char: 'D', type: 'maj', num: 'II'},
    {char: 'E', type: 'min', num: 'iii'},
    {char: 'F♯', type: 'dim', num: 'iv°'},
    {char: 'G', type: 'maj', num: 'V'},
    {char: 'A', type: 'min', num: 'vi'},
    {char: 'B', type: 'min', num: 'vii'}
  ],
  ionian: [
    {char: 'C', type: 'maj', num: 'I'},
    {char: 'D', type: 'min', num: 'ii'},
    {char: 'E', type: 'min', num: 'iii'},
    {char: 'F', type: 'maj', num: 'IV'},
    {char: 'G', type: 'maj', num: 'V'},
    {char: 'A', type: 'min', num: 'vi'},
    {char: 'B', type: 'dim', num: 'vii°'}
  ],
  mixolydian: [
    {char: 'C', type: 'maj', num: 'I'},
    {char: 'D', type: 'min', num: 'ii'},
    {char: 'E', type: 'dim', num: 'iii°'},
    {char: 'F', type: 'maj', num: 'IV'},
    {char: 'G', type: 'min', num: 'v'},
    {char: 'A', type: 'min', num: 'vi'},
    {char: 'B♭', type: 'maj', num: 'VII'}
  ],
  dorian: [
    {char: 'C', type: 'min', num: 'i'},
    {char: 'D', type: 'min', num: 'ii'},
    {char: 'E♭', type: 'maj', num: 'III'},
    {char: 'F', type: 'maj', num: 'IV'},
    {char: 'G', type: 'min', num: 'v'},
    {char: 'A', type: 'dim', num: 'vi°'},
    {char: 'B♭', type: 'maj', num: 'VII'}
  ],
  aeolian: [
    {char: 'C', type: 'min', num: 'i'},
    {char: 'D', type: 'dim', num: 'ii°'},
    {char: 'E♭', type: 'maj', num: 'III'},
    {char: 'F', type: 'min', num: 'iv'},
    {char: 'G', type: 'min', num: 'v'},
    {char: 'A♭', type: 'maj', num: 'VI'},
    {char: 'B♭', type: 'maj', num: 'VII'}
  ],
  phrygian: [
    {char: 'C', type: 'min', num: 'i'},
    {char: 'D♭', type: 'maj', num: 'II'},
    {char: 'E♭', type: 'maj', num: 'III'},
    {char: 'F', type: 'min', num: 'iv'},
    {char: 'G', type: 'dim', num: 'v°'},
    {char: 'A♭', type: 'maj', num: 'VI'},
    {char: 'B♭', type: 'min', num: 'vii'}
  ],
  locrian: [
    {char: 'C', type: 'dim', num: 'i°'},
    {char: 'D♭', type: 'maj', num: 'II'},
    {char: 'E♭', type: 'min', num: 'iii'},
    {char: 'F', type: 'min', num: 'iv'},
    {char: 'G♭', type: 'maj', num: 'V'},
    {char: 'A♭', type: 'maj', num: 'VI'},
    {char: 'B♭', type: 'min', num: 'vii'}
  ],
}

const ScaleHeader = ({text}: {text: string}) => (
  <div className='headerRow'>
    <span className='modeTag'>{text}</span>
  </div>
)

const ScaleRow = ({array}: {array: Array<{char: string, type: string, num: string}>}) => (
  <div className='contentRow'>
    { array.map(chord => <NoteCard key={chord} chord={chord}/>) }
  </div>
)

const NoteCard = ({chord}: {chord: {char: string, type: string, num: string}}) => (
  <div className='card'>
    <div className={'cardContent ' + chord.type} >
      <div className='cardNumber'>{chord.num}</div>
      <div className='cardText'>{chord.char[0]}<span className='accidental'>{chord.char[1]}</span></div>
    </div>
    <div className='cardShadow' />
  </div>
)

export const Chart = () => {

  const [scales, setScales] = useState<{[key: string]: Array<{char: string, type: string, num: string}>} | undefined>(undefined)

  useEffect(() => {
    const scalesSpelled = pitchSpeller(defaultScales, 0)
    // console.log(Object.keys(scalesSpelled))
    console.log(scalesSpelled)

    setScales(exampleOutput)
  }, []);

  return (
    <div className='chart blur'>
      { 
        scales ? (
          <>
            <div className='chartHeaders'>
              { Object.keys(scales).map(scaleName => <ScaleHeader text={scaleName} />) }
            </div>
            <div className='chartContent'>
              { Object.values(scales).map(scaleChars => <ScaleRow array={scaleChars} />) }
            </div>
          </>
        ) : <></>
      }
    </div>
  );
}
