import { useContext, useState, useEffect } from 'react';
import { SortableContext, horizontalListSortingStrategy, useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { v4 as uuidv4 } from 'uuid';

import './Bar.css';

import { NoteCard } from '../NoteCard';
import { ProgContext } from '../ModeContext';
import { useDroppable } from '@dnd-kit/core';


const SortableItem = ({item, setFunc}: {item: any, setFunc?: any}) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({id: item.id, disabled: true, data: {setFunc: setFunc}})
  // } = useSortable({id: item.id, })

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  }

  if (!item || !setFunc ) {
    return (
      <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
        <div className='emptyCard'/>
    </div>
    )
  }

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <NoteCard chord={item}/>
    </div>
  )
};


export const Bar = () => {

  const [items, setItems] = useState<Array<{id: string, char: string, type: string, num: string}>>([
    {id: uuidv4(), char: 'D', type: 'min', num: 'ii'},
    {id: uuidv4(), char: 'G', type: 'maj', num: 'V'},
    {id: uuidv4(), char: 'C', type: 'maj', num: 'I'},
  ]);

  const [uniqueBarId] = useState('sortable-' + uuidv4());

  return (
    <SortableContext
      items={items.map((i: any) => i.id)}
      strategy={horizontalListSortingStrategy} 
      id={uniqueBarId}
    >
      <div className='bar barBlur' style={{backgroundColor: 'magenta'}}>
        {
          items.length
            ? items.map((chord: any) => <SortableItem item={chord} setFunc={setItems}/>)
            // : <div className='emptyCard'>+</div>
            : <SortableItem item={{id: 'empty'}}/>

        }
      </div>
    </SortableContext>
  );
}
