import { v4 as uuidv4 } from 'uuid';
import { chordIntervals } from './chordIntervals';

// const defScaleObject = {
//   "Diatonic": {
//     "lydian":     [0,2,4,6,7,9,11], 
//     "ionian":     [0,2,4,5,7,9,11], 
//     "mixolydian": [0,2,4,5,7,9,10], 
//     "dorian":     [0,2,3,5,7,9,10], 
//     "aeolian":    [0,2,3,5,7,8,10], 
//     "phrygian":   [0,1,3,5,7,8,10], 
//     "locrian":    [0,1,3,5,6,8,10],
//   },
// }

interface Chord {
  id: string;
  char: string;
  num: string;
  type: {
    full: string;
    short: string;
    symbol: string;
  }
}

export class ModeList {
  modeObj: {[key: string]: Array<number>};
  transpose: number;

  constructor(modeObj: {[key: string]: Array<number>} = {}, transpose: number = 0) {

    this.modeObj = {};
    this.transpose = transpose;

    // Object.entries(modeObj).forEach(([name, recipe]) => console.log(recipe));
    Object.entries(modeObj).forEach(([name, recipe]) => this.addMode(name, recipe));

  }

  setObject(modeObj: {[key: string]: Array<number>}, transpose: number) { 
    this.modeObj = modeObj;
    this.transpose = transpose;
  }

  addMode(scaleName: string, scaleRecipe: Array<number>) {
    this.modeObj[scaleName] = scaleRecipe;
  }
}

class Mode {
  modeName: string;
  modeArray: Array<Object>;

  constructor(modeName: string) {
    this.modeName = modeName;
    this.modeArray = [];
  }

  addChord(chord: Object) { this.modeArray.push(chord) }
}
