import { useState, useEffect, createContext } from 'react';

import { v4 as uuidv4 } from 'uuid';

import { DndContext, DragOverlay, rectIntersection, useDraggable, useDroppable } from '@dnd-kit/core';
import { CSS } from '@dnd-kit/utilities';
import { arrayMove, SortableContext, horizontalListSortingStrategy, useSortable } from '@dnd-kit/sortable';


export const ProgContext = createContext<any>(undefined);


export const ModeContext = ({children}: {children: any}) => {

  const [items, setItems] = useState<Array<{id: string, char: string, type: string, num: string}>>([
    {id: 'id2', char: 'D', type: 'min', num: 'ii'},
    {id: 'id5', char: 'G', type: 'maj', num: 'V'},
    {id: 'id1', char: 'C', type: 'maj', num: 'I'},
  ]);

  const [matrix, setMatrix] = useState<any>(undefined);

  const [dragId, setDragId] = useState<string | undefined>(undefined);


  const handleDragOver = ({active, over}: any) => {

    if (!over || !over.data.current) {
      // if object hovered over is not sortable
      return
    };

    if (active.data.current.payload) {
      // if draggable is from table, NOT from sortable

      const payload = active.data.current.payload;
      const {index: newIndex, containerId: newContainerId } = over.data.current.sortable;

      const duplicateIndex = items.findIndex(i => i.id === dragId);

      if (duplicateIndex === -1) {
        // dragId not already in sortable

        setItems(items => [...items, {...payload, id: dragId}]);

        // setMatrix((matrix) => matrix.map(item => { return (item.id === dragId) ? {...item, id: uuidv4()} : item }));

      } else {
        setItems(items => arrayMove(items, duplicateIndex, newIndex));
      };

      return
    };
  }


  const handleDragEnd = ({active, over}: any) => {

    if (!over) {
      if (active.data.current.sortable) {
        // remove from sortable
        console.log('remove from sortable');

        const oldIndex = active.data.current.sortable.index;
        setItems((items) => items.slice(0, oldIndex).concat(items.slice(oldIndex + 1)));

        return
      };

      // cancel if drop invalid
      console.log('drop location invalid');
      return
    };

    // if (over.data.current.sortable) {
    if (active.data.current.sortable && over.data.current.sortable) {

      console.log('sortable -> sortable')

      const oldIndex = active.data.current.sortable.index;
      const newIndex = over.data.current.sortable.index;

      setItems((items) => arrayMove(items, oldIndex, newIndex));
    }
  };


  return (
    <ProgContext.Provider value={{items: items, matrix: matrix}}>
      <DndContext
        onDragStart={({active}: any) => {setDragId(active.id)}}
        onDragCancel={() => {setDragId(undefined)}}
        onDragOver={handleDragOver}
        onDragEnd={handleDragEnd}
        collisionDetection={rectIntersection}
      >
        {children}
        <DragOverlay>
          <div className='cardContent maj'>
            <div className='cardNumber'></div>
            <div className='cardText'></div>
          </div>
        </DragOverlay>
      </DndContext>
    </ProgContext.Provider>
  );
}
