import { v4 as uuidv4 } from 'uuid';

import './Chart.css';
import { useState, useEffect } from 'react';
import { pitchSpeller } from './pitchSpeller';

const defaultScales = { 
  "DIA": {        // diatonic
    "lydian":     [0,2,4,6,7,9,11], 
    "ionian":     [0,2,4,5,7,9,11], 
    "mixolydian": [0,2,4,5,7,9,10], 
    "dorian":     [0,2,3,5,7,9,10], 
    "aeolian":    [0,2,3,5,7,8,10], 
    "phrygian":   [0,1,3,5,7,8,10], 
    "locrian":    [0,1,3,5,6,8,10],
  },
  "H.MIN": {       // harmonic minor (#7)
    "lydian â™¯2":     [0,3,4,6,7,9,11],
    "ionian â™¯5":     [0,2,4,5,8,9,11],
    "altered ð„«7":    [0,1,3,4,6,8,9],
    "ukr. dorian":   [0,2,3,6,7,9,10],
    "harm. minor":   [0,2,3,5,7,8,11], 
    "phrygian dom.": [0,1,4,5,7,8,10],
    "locrian â™®6":    [0,1,3,5,6,9,10],
  }
}

const exampleOutput = {
  lydian: [
    {id: uuidv4(), char: 'C', type: 'maj', num: 'I'},
    {id: uuidv4(), char: 'D', type: 'maj', num: 'II'},
    {id: uuidv4(), char: 'E', type: 'min', num: 'iii'},
    {id: uuidv4(), char: 'Fâ™¯', type: 'dim', num: 'ivÂ°'},
    {id: uuidv4(), char: 'G', type: 'maj', num: 'V'},
    {id: uuidv4(), char: 'A', type: 'min', num: 'vi'},
    {id: uuidv4(), char: 'B', type: 'min', num: 'vii'}
  ],
  ionian: [
    {id: uuidv4(), char: 'C', type: 'maj', num: 'I'},
    {id: uuidv4(), char: 'D', type: 'min', num: 'ii'},
    {id: uuidv4(), char: 'E', type: 'min', num: 'iii'},
    {id: uuidv4(), char: 'F', type: 'maj', num: 'IV'},
    {id: uuidv4(), char: 'G', type: 'maj', num: 'V'},
    {id: uuidv4(), char: 'A', type: 'min', num: 'vi'},
    {id: uuidv4(), char: 'B', type: 'dim', num: 'viiÂ°'}
  ],
  mixolydian: [
    {id: uuidv4(), char: 'C', type: 'maj', num: 'I'},
    {id: uuidv4(), char: 'D', type: 'min', num: 'ii'},
    {id: uuidv4(), char: 'E', type: 'dim', num: 'iiiÂ°'},
    {id: uuidv4(), char: 'F', type: 'maj', num: 'IV'},
    {id: uuidv4(), char: 'G', type: 'min', num: 'v'},
    {id: uuidv4(), char: 'A', type: 'min', num: 'vi'},
    {id: uuidv4(), char: 'Bâ™­', type: 'maj', num: 'VII'}
  ],
  dorian: [
    {id: uuidv4(), char: 'C', type: 'min', num: 'i'},
    {id: uuidv4(), char: 'D', type: 'min', num: 'ii'},
    {id: uuidv4(), char: 'Eâ™­', type: 'maj', num: 'III'},
    {id: uuidv4(), char: 'F', type: 'maj', num: 'IV'},
    {id: uuidv4(), char: 'G', type: 'min', num: 'v'},
    {id: uuidv4(), char: 'A', type: 'dim', num: 'viÂ°'},
    {id: uuidv4(), char: 'Bâ™­', type: 'maj', num: 'VII'}
  ],
  aeolian: [
    {id: uuidv4(), char: 'C', type: 'min', num: 'i'},
    {id: uuidv4(), char: 'D', type: 'dim', num: 'iiÂ°'},
    {id: uuidv4(), char: 'Eâ™­', type: 'maj', num: 'III'},
    {id: uuidv4(), char: 'F', type: 'min', num: 'iv'},
    {id: uuidv4(), char: 'G', type: 'min', num: 'v'},
    {id: uuidv4(), char: 'Aâ™­', type: 'maj', num: 'VI'},
    {id: uuidv4(), char: 'Bâ™­', type: 'maj', num: 'VII'}
  ],
  phrygian: [
    {id: uuidv4(), char: 'C', type: 'min', num: 'i'},
    {id: uuidv4(), char: 'Dâ™­', type: 'maj', num: 'II'},
    {id: uuidv4(), char: 'Eâ™­', type: 'maj', num: 'III'},
    {id: uuidv4(), char: 'F', type: 'min', num: 'iv'},
    {id: uuidv4(), char: 'G', type: 'dim', num: 'vÂ°'},
    {id: uuidv4(), char: 'Aâ™­', type: 'maj', num: 'VI'},
    {id: uuidv4(), char: 'Bâ™­', type: 'min', num: 'vii'}
  ],
  locrian: [
    {id: uuidv4(), char: 'C', type: 'dim', num: 'iÂ°'},
    {id: uuidv4(), char: 'Dâ™­', type: 'maj', num: 'II'},
    {id: uuidv4(), char: 'Eâ™­', type: 'min', num: 'iii'},
    {id: uuidv4(), char: 'F', type: 'min', num: 'iv'},
    {id: uuidv4(), char: 'Gâ™­', type: 'maj', num: 'V'},
    {id: uuidv4(), char: 'Aâ™­', type: 'maj', num: 'VI'},
    {id: uuidv4(), char: 'Bâ™­', type: 'min', num: 'vii'}
  ],
}

const ScaleHeader = ({text}: {text: string}) => (
  <div className='headerRow'>
    <span className='modeTag'>{text}</span>
  </div>
)

const ScaleRow = ({array}: {array: Array<{char: string, type: string, num: string}>}) => (
  <div className='contentRow'>
    { array.map(chord => <NoteCard chord={chord}/>) }
  </div>
)

const NoteCard = ({chord}: {chord: {char: string, type: string, num: string}}) => (
  <div className='card'>
    <div className={'cardContent ' + chord.type} >
      <div className='cardNumber'>{chord.num}</div>
      <div className='cardText'>{chord.char[0]}<span className='accidental'>{chord.char[1]}</span></div>
    </div>
    <div className='cardShadow' />
  </div>
)

export const Chart = () => {

  const [scales, setScales] = useState<{[key: string]: Array<{id: string, char: string, type: string, num: string}>} | undefined>(undefined)

  useEffect(() => {
    const scalesSpelled = pitchSpeller(defaultScales, 0)
    console.log(scalesSpelled)

    setScales(scalesSpelled)
  }, []);

  return (
    <div className='chart blur'>
      { 
        scales ? (
          <>
            <div className='chartHeaders'>
              { Object.keys(scales).map(scaleName => <ScaleHeader text={scaleName} />) }
            </div>
            <div className='chartContent'>
              { Object.values(scales).map(scaleChars => <ScaleRow array={scaleChars} />) }
            </div>
          </>
        ) : <></>
      }
    </div>
  );
}
