// This file was mostly written by chatGPT

interface ChordTrieNode {
  children: { [interval: number]: ChordTrieNode };
  isChordEnd: boolean;
  chordType: string | null;
}

export default class ChordTrie {
  root: ChordTrieNode;

  constructor() {
    this.root = { children: {}, isChordEnd: false, chordType: null };
  }

  insertChord(intervals: number[], chordType: string) {
    let currentNode: ChordTrieNode = this.root;

    for (const interval of intervals) {
      if (!currentNode.children[interval]) {
        currentNode.children[interval] = { children: {}, isChordEnd: false, chordType: null };
      }
      currentNode = currentNode.children[interval];
    }

    currentNode.isChordEnd = true;
    currentNode.chordType = chordType;
  }

  findChord(intervals: number[]): string | null {
    let currentNode: ChordTrieNode = this.root;

    for (const interval of intervals) {
      if (!currentNode.children[interval]) {
        return null; // Chord not found
      }
      currentNode = currentNode.children[interval];
    }

    return currentNode.isChordEnd ? currentNode.chordType : null;
  }
}
