import './NoteCard.css';

export const NoteCard = ({chord, extend=false}: {chord: {id: string, char: string, type: {full: string, short: string, symbol: string}, num: string}, extend?: boolean}) => {  
  return (
    <div className={'cardContent ' + chord.type.short} style={{width: extend ? "50px" : "25px"}}>
      <div className='cardNumber'>{chord.num}</div>
      <div className='textContainer'>
        <div className='rootText'>{chord.char[0]}{chord.char.slice(1,)}</div>
        {extend ? <div className='seventhText'>{chord.type.symbol}</div> : <></>}
      </div>
    </div>
  )
}
